   // proste logowanie bez zadnego szyfrowania
    public void open()
    {
        String has = new String (haslo.getPassword());
 
        try
        {
            if (nick.getText().equals("POSK") && has.equals("posk"))
            {
            Stan st = new Stan();
            st.setVisible(true);
            dispose();
            }
            else
            {
                nick.setText("Ty tu nie pracujesz");
            }
        }
        catch (Exception e)
        {
            nick.setText("Podaj poprawne dane");
        }
    }
    
    
